import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angularpipes';
  firstName:string="Kishore";
  lastName:string="K"
  joinDate:any=new Date();
  salary:number=20000.6783;

}
Your Name :{{firstName+" "+lastName|uppercase}}<br>
Your Name :{{firstName+" "+lastName|lowercase}}<br>
Your Name :{{firstName+" "+lastName|slice:4}}<br>
Your Name :{{firstName+" "+lastName|slice:2:6}}<br>
Join Date :<b>{{joinDate|date}}</b><br>
Join Date :<b>{{joinDate|date:'fullDate'}}</b><br>
Join Date :<b>{{joinDate|date:'shortdate'}}</b><br>
Join Date :<b>{{joinDate|date:'fullTime'}}</b><br>
