Join Date :<b>{{joinDate|date:'mm/yyyy/dd'}}</b><br>
Join Date :<b>{{joinDate|date:'mm/yy/dd h:mi:ss am'}}</b><br>
Join Date :<b>{{joinDate|date:'hh:m:ss am z'}}</b><br>

Salary <b>{{salary}}</b>


