import { Component } from '@angular/core';

@Component({
  selector: 'app-department-list',
  templateUrl: './department-list.component.html',
  styleUrls: ['./department-list.component.css']
})
export class DepartmentListComponent {
  deptlist:any=[{deptId:'PR',deptName:'Public Relations'},
{deptId:'RD',deptName:'Research & Development'},
{deptId:'CSR',deptName:'Corporate School Responsibility'},
]
}
<b>Department List</b>
<table style="display:grid;border:3px dotted;">
    <tr><td>Department Id</td><td>Department Name</td></tr>
    <tr *ngFor="let dept of deptlist"><td>{{dept.deptId}}</td><td>{{dept.deptName}}</td>
    </tr>
</table>
<p>department-list works!</p>
