import { Component } from '@angular/core';
import { EmpManagementService } from './EmpManagementService';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'structureddata';

  emplist=[
    {empId:1,firstName:"Kishore",lastName:"K",age:21,location:"Chennai"},
    {empId:2,firstName:"Yogesh",lastName:"R",age:21,location:"Chennai"},
    {empId:3,firstName:"Jhonny",lastName:"D'cruse",age:21,location:"Chennai"}, 
  ];

  empvc:EmpManagementService=new EmpManagementService() ;

  emp= {empId:5,irstName:"Kishore",lastName:"K",age:21,location:"Chennai"}

  addEmp(){

    this.empvc.addEmp(this.emp,this.emplist)
    // this.emplist.push({firstName:this.emp.firstName,lastName:this.emp.lastName,
    //   age:this.emp.age,location:this.emp.location})
    // alert("One Employee Data is added....");
  }

}
First Name {{emp.firstName}}<br>
Last Name {{emp.lastName}}<br>
Age {{emp.age}}<br>
Location {{emp.location}}<br>

Your first name <input type="text" [(ngModel)]="emp.firstName"/><br>
Your last name <input type="text" [(ngModel)]="emp.lastName"/><br>
Your Age <input type="numberf" [(ngModel)]="emp.age"/><br>
Your location <input type="text" [(ngModel)]="emp.location"/><br>
<!-- <input type="button" value="App data" (click)="emplist.push(emp)"/> -->
<input type="button" value="App data" (click)="addEmp()"/><br>

<table style="border:5px;border-style: dotted;">
  <thead>
  <td><b>First Name</b></td>
  <td><b>Last Name</b></td>
  <td><b>Age</b></td>
  <td><b>Location</b></td></thead>

  <tr *ngFor="let i of emplist">
    <td>{{i.firstName}}</td>
    <td>{{i.lastName}}</td>
    <td>{{i.age}}</td>
    <td>{{i.location}}</td>

  </tr>

</table>
