import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'usermanagement';
  userId:string="Unknown";
  password:string="unknown";

  constructor(private hclient:HttpClient)
  {
    console.log("HttpClient created....");
  }
  tryAuthenticate(){
    window.alert("good");
  }
}

<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Document</title>
</head>
<body>
        <b>Please sign in to proceed further</b><br>
        <form *ngSubmit="tryAuthenticate()">
            <legend>
                User ID<input type="text" name="userId" [(ngModel)]="userId" placeholder="Enter your user id"/><br><br>
                Password<input type="password" name="password" [(ngModel)]="password" placeholder="Enter your password"/><br><br>
            </legend>
            <legend>
                <input type="submit" value="Sign in"/>&nbsp;&nbsp;<input type="reset" value="Reset"/>
            </legend>
        </form>
</body>
</html>
<router-outlet></router-outlet>
