import { Component } from '@angular/core';
import { User } from './user';
import axios from 'axios';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'usermanagement';
  userId:string="";
  password:string="";
  emailId:string="";
  // constructor(private hclient:HttpClient)
  // {
  //   console.log("HttpClient created....");
  // }
  // tryAuthenticate(){
  //   window.alert("Contacting the backend....");
  //   this.hclient.get("http://localhost:7500/");
  // }
  constructor(public user:User)
  {}
  addAUser(){
    axios.post("http://localhost:9000/addUser",this.user);
    window.alert("Trying to add a user");
  }
}

<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Document</title>
</head>
<body>
        <b>Please sign in to proceed further</b><br>
        <form (ngSubmit)="addAUser()">
            <legend>
                User ID<input type="text" name="userid" [(ngModel)]="user.userid" placeholder="Enter your user id"/><br><br>
                Password<input type="password" name="password" [(ngModel)]="user.password" placeholder="Enter your password"/><br><br>
                Email<input type="email" name="emailId" [(ngModel)]="user.emailId" placeholder="Enter your mail id"/><br><br>
            </legend>
            <legend>
                <input type="submit" value="Add User"/>&nbsp;&nbsp;<input type="reset" value="Reset"/>
            </legend>
        </form>
</body>
</html>
<router-outlet></router-outlet>
